func chooseName() {

    var currentLevel = 0
    var finalLevel = 5

    var levelPassed: Bool = GameLevel.didPassLevel
    //GameLevel is a class not shown in this code. It has a member didPassLevel that indicates whether the user has succeeded in the current level

    while (currentLevel <= finalLevel) {
        if levelPassed == false {
            print("Oh no! Try again!")
        } else if currentLevel == 5 {
            print("You won the game!")
        } else {
            print("Play next level!")
            currentLevel += 1
        }
    }
}

